import{tpaCommand}from"./commands/tpa";import{helpCommand}from"./commands/help";class CommandClass{constructor(){this.functions=[],this.addFunction(tpaCommand),this.addFunction(helpCommand)}run(t,n,...s){var a=this.getFunctions().find(n=>n.name===t);if(!a)return n.sendMessage("未知命令");a.handler(n,s)}addFunction({name:n,desc:t,usage:s,handler:a}){this.functions.push({name:n,desc:t,usage:s,handler:a})}removeFunction(t){this.functions=this.functions.filter(n=>n.name!==t)}getFunction(t){return this.functions.find(n=>n.name===t)}getFunctions(){return this.functions}}export default new CommandClass;